CREATE TABLE IF NOT EXISTS `t_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `title` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '标题',
  `content` text COLLATE utf8_unicode_ci NOT NULL COMMENT '文章内容',
  `columnid` int(11) NOT NULL DEFAULT '1' COMMENT '文章分类ID',
  `keywords` varchar(100) COLLATE utf8_unicode_ci NOT NULL COMMENT '关键词',
  `author` varchar(50) COLLATE utf8_unicode_ci NOT NULL COMMENT '作者',
  `image` varchar(200) COLLATE utf8_unicode_ci NOT NULL COMMENT '图片地址',
  `ishot` int(11) NOT NULL DEFAULT '0' COMMENT '是否推荐',
  `clicks` int(11) NOT NULL DEFAULT '0',
  `addtime` timestamp NULL DEFAULT CURRENT_TIMESTAMP COMMENT '添加时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=15 ;

